import shutilimport osPASS_XML = os.path.join(os.path.dirname(__file__) or os.getcwd(), 'output_pass.xml')FAIL_XML = os.path.join(os.path.dirname(__file__) or os.getcwd(), 'output_fail.xml')def putResult(outPath, result):    if result == "FAILED":        shutil.copyfile(FAIL_XML, os.path.join(outPath,"output.xml"))    elif result == "PASS":        shutil.copyfile(PASS_XML, os.path.join(outPath,"output.xml"))if __name__=='__main__':    #putResult(r"c:\tmp\Agent", "FAILED")    putResult(r"c:\tmp\Agent", "PASS")